package action;import data.TodoDataServices;import java.util.HashMap;import java.util.Map;public class TodoHelper {    private TodoDataServices dataServices = new TodoDataServices();    public Map<String,Object> createTask (String type){        Map<String,Object> todoMap = new HashMap<>();        todoMap.put("status",true);        todoMap.put("type",type);        dataServices.saveTask(todoMap);        return todoMap;    }}