package data;import com.google.appengine.api.datastore.DatastoreService;import com.google.appengine.api.datastore.DatastoreServiceFactory;import com.google.appengine.api.datastore.Entity;import java.util.Map;public class TodoDataServices{   private DatastoreService datastore = DatastoreServiceFactory.getDatastoreService();;   public Entity saveTask (Map todoMap){      Entity todo = new Entity("Todo", "pravantestkey");      todo.setProperty("name","pravanjan");      todo.setProperty("type",todoMap.get("type"));      datastore.put(todo);      return todo;   }}